mapper.insert:

    SELECT NVL(MAX(TO_NUMBER(GROUP_CODE)),00000001)+1 as group_code FROM NFDS_USER_GROUP

INSERT INTO NFDS_USER_GROUP ( 
      GROUP_CODE 
    , GROUP_NAME
    , GROUP_COMMENT
    , RGDATE
    , RGNAME
) VALUES (
      #{group_code}
    , #{group_name}
    , #{group_comment}
    , SYSDATE
    , #{rgname}
)

mapper.update:
UPDATE NFDS_USER_GROUP SET
      GROUP_NAME    = #{group_name}
    , GROUP_COMMENT = #{group_comment}
    , UPDATE_TIME   = SYSDATE
    , MODDATE       = SYSDATE
    , MODNAME       = #{modname}
WHERE GROUP_CODE = #{group_code}

mapper.select:
SELECT COUNT(*)
FROM NFDS_USER
WHERE GROUP_CODE = #{group_code}

mapper.insert:
INSERT INTO NFDS_USER_MENU_AUTH (
      GROUP_CODE 
    , MNUCOD
) VALUES ( 
      #{group_code}
    , #{mnucod}
)
