package nurier.scraping.member.project.model.VO;

import java.sql.Date;

public class ProjectVO {
	private String pCode;			// 프로젝트 코드
	private String pSubject;		// 프로젝트 명
	private String pIncharge;		// 현장대리인
	private String productCode;		// 사업유형
	private String customerCode;	// 거래처
	private Date pContDate;			// 계약일
	private Date pConts;			// 계약 기간 시작
	private Date pConte;			// 계약 기간 끝
	private String PRemarks;		// 특이사항 및 비고
	private String pWriter;			// 등록자
	private Date pRegDate;			// 등록일
	
	
	public ProjectVO() {
		super();
		// TODO Auto-generated constructor stub
	}


	public ProjectVO(String pCode, String pSubject, String pIncharge, String productCode, String customerCode,
			Date pContDate, Date pConts, Date pConte, String pRemarks, String pWriter, Date pRegDate) {
		super();
		this.pCode = pCode;
		this.pSubject = pSubject;
		this.pIncharge = pIncharge;
		this.productCode = productCode;
		this.customerCode = customerCode;
		this.pContDate = pContDate;
		this.pConts = pConts;
		this.pConte = pConte;
		PRemarks = pRemarks;
		this.pWriter = pWriter;
		this.pRegDate = pRegDate;
	}


	public String getpCode() {
		return pCode;
	}


	public void setpCode(String pCode) {
		this.pCode = pCode;
	}


	public String getpSubject() {
		return pSubject;
	}


	public void setpSubject(String pSubject) {
		this.pSubject = pSubject;
	}


	public String getpIncharge() {
		return pIncharge;
	}


	public void setpIncharge(String pIncharge) {
		this.pIncharge = pIncharge;
	}


	public String getProductCode() {
		return productCode;
	}


	public void setProductCode(String productCode) {
		this.productCode = productCode;
	}


	public String getCustomerCode() {
		return customerCode;
	}


	public void setCustomerCode(String customerCode) {
		this.customerCode = customerCode;
	}


	public Date getpContDate() {
		return pContDate;
	}


	public void setpContDate(Date pContDate) {
		this.pContDate = pContDate;
	}


	public Date getpConts() {
		return pConts;
	}


	public void setpConts(Date pConts) {
		this.pConts = pConts;
	}


	public Date getpConte() {
		return pConte;
	}


	public void setpConte(Date pConte) {
		this.pConte = pConte;
	}


	public String getPRemarks() {
		return PRemarks;
	}


	public void setPRemarks(String pRemarks) {
		PRemarks = pRemarks;
	}


	public String getpWriter() {
		return pWriter;
	}


	public void setpWriter(String pWriter) {
		this.pWriter = pWriter;
	}


	public Date getpRegDate() {
		return pRegDate;
	}


	public void setpRegDate(Date pRegDate) {
		this.pRegDate = pRegDate;
	}
	
}
